import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Login.css';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [userType, setUserType] = useState('buyer');
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    setError('');
    setIsLoading(true);

    setTimeout(() => {
      // Mock authentication
      const mockUsers = {
        buyer: [
          { email: 'test@test.com', password: 'test123', name: 'Test User' },
          { email: 'buyer@example.com', password: 'password123', name: 'John Buyer' }
        ],
        seller: [
          { email: 'seller@example.com', password: 'seller123', name: 'Jane Seller' }
        ]
      };

      const users = mockUsers[userType];
      const user = users.find(u => u.email === email && u.password === password);
      
      if (user) {
        // Store user info in localStorage
        localStorage.setItem('user', JSON.stringify({
          email: user.email,
          name: user.name,
          type: userType,
          isAuthenticated: true,
          token: 'mock-jwt-token-' + Date.now()
        }));
        
        // REDIRECT TO HOLIDAY STORE (different project)
        // Note: This will be a different URL/port
        window.location.href = 'http://localhost:3001'; // Holiday Store project port
      } else {
        setError('Invalid email or password');
        setIsLoading(false);
      }
    }, 1000);
  };

  const handleDemoLogin = (type) => {
    setIsLoading(true);
    setError('');
    
    setTimeout(() => {
      const demoUser = type === 'buyer' 
        ? { email: 'test@test.com', password: 'test123', name: 'Demo Buyer' }
        : { email: 'seller@example.com', password: 'seller123', name: 'Demo Seller' };
      
      localStorage.setItem('user', JSON.stringify({
        email: demoUser.email,
        name: demoUser.name,
        type: type,
        isAuthenticated: true,
        token: 'demo-token'
      }));
      
      // REDIRECT TO HOLIDAY STORE for buyer
      if (type === 'buyer') {
        window.location.href = 'http://localhost:3001'; // Holiday Store project
      } else {
        window.location.href = 'http://localhost:3002'; // Seller dashboard (different project)
      }
    }, 800);
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <h2 className="login-title">Welcome Back</h2>
        <p className="login-subtitle">Please enter your credentials to continue</p>
        
        <div className="user-type-selector">
          <button 
            type="button"
            className={`user-type-btn ${userType === 'buyer' ? 'active' : ''}`}
            onClick={() => setUserType('buyer')}
          >
            <i className="fas fa-shopping-cart"></i> Buyer
          </button>
          <button 
            type="button"
            className={`user-type-btn ${userType === 'seller' ? 'active' : ''}`}
            onClick={() => setUserType('seller')}
          >
            <i className="fas fa-store"></i> Seller
          </button>
        </div>

        {error && <div className="error-message">{error}</div>}

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="email">Email Address</label>
            <input
              type="email"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Enter your email"
              required
              disabled={isLoading}
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter your password"
              required
              disabled={isLoading}
            />
          </div>

          <button 
            type="submit" 
            className="login-btn"
            disabled={isLoading}
          >
            {isLoading ? (
              <>
                <i className="fas fa-spinner fa-spin"></i> Signing in...
              </>
            ) : (
              <>
                <i className="fas fa-sign-in-alt"></i> Sign In
              </>
            )}
          </button>
        </form>

        <div className="demo-login-section">
          <p className="demo-title">Quick Demo Login:</p>
          <div className="demo-buttons">
            <button 
              type="button"
              className="demo-btn buyer-demo"
              onClick={() => handleDemoLogin('buyer')}
              disabled={isLoading}
            >
              <i className="fas fa-user"></i> Demo Buyer
            </button>
            <button 
              type="button"
              className="demo-btn seller-demo"
              onClick={() => handleDemoLogin('seller')}
              disabled={isLoading}
            >
              <i className="fas fa-user-tie"></i> Demo Seller
            </button>
          </div>
        </div>

        <div className="login-footer">
          <p>Don't have an account? <a href="#" className="signup-link">Sign up here</a></p>
          <p><a href="#" className="forgot-link">Forgot password?</a></p>
        </div>
      </div>
    </div>
  );
};

export default Login;
